'use strict';

var chalk = require('chalk');

// based on https://stackoverflow.com/questions/4810841/how-can-i-pretty-print-json-using-javascript
// using CLI colors, instead

function jsoncolor(json) {
  if (typeof json !== 'string') {
    json = JSON.stringify(json, undefined, 2);
  }
  return json.replace(/("(\\u[a-zA-Z0-9]{4}|\\[^u]|[^\\"])*"(\s*:)?|\b(true|false|null)\b|-?\d+(?:\.\d*)?(?:[eE][+\-]?\d+)?)/g, function (match) {
    var cls = 'number';
    if (/^"/.test(match)) {
      if (/:$/.test(match)) {
        cls = 'key';
      } else {
        cls = 'string';
      }
    } else if (/true|false/.test(match)) {
      cls = 'boolean';
    } else if (/null/.test(match)) {
      cls = 'null';
    }
    return jsoncolor.colors[cls](match);
  });
}

jsoncolor.colors = {
  number: chalk.hex('#ff6188'),
  key: chalk.hex('#ffd866'),
  string: chalk.hex('#a9dc76'),
  boolean: chalk.hex('#ab9df2'),
  null: chalk.hex('#ab9df2')
};

module.exports = jsoncolor;